export function AtAGlance() {
  return (
    <div className="grid grid-cols-2 gap-3 lg:grid-cols-4 lg:gap-5">
      {items.map((item, index) => (
        <Item {...item} key={index} />
      ))}
    </div>
  );
}

function Item({
  image,
  title,
}: {
  image: React.ReactNode;
  title: React.ReactNode;
}) {
  return (
    <div className="flex flex-col rounded-md bg-black lg:rounded-lg">
      <div className="flex aspect-[150/188] items-center justify-center lg:aspect-[254/243]">
        {image}
      </div>

      <div className="my-auto flex items-center justify-center pb-5 text-center text-sm font-normal text-index-light-grey lg:text-base">
        {title}
      </div>
    </div>
  );
}
